sp_widget.script:
(function(){
	var gr = $sp.getInstanceRecord();
	data.href = $sp.getMenuHREF(gr);
	data.target = options.target || "";

	var currentDate = new GlideDateTime().getDate;
	var gdt = new GlideDateTime();
	var month = gdt.getMonthLocalTime();

	var monthName = new sn_hr_sp.ihr_todayWidgetUtils().getMonthName(month, true);
	data.month = monthName;
	data.day = gdt.getDayOfMonthLocalTime();

	var weather = new sn_hr_sp.ihr_todayWidgetUtils().getWeather_byCityId();
	data.weather = weather;

	//data.temperature = weather.temperature;
	//data.weather_id = weather.weather_id;
	//data.weather_desc = weather.weather_desc;
	data.weather_image = 'ihr_today_' + weather.weather_icon_id + '.png';
})();
